package in.co.rays.basic;

public class Tables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int tab;
		for (int i =2; i <=9; i++) {
			for (int j = 1; j <=10; j++) {
				tab=i*j;
				System.out.print(" "+tab);
				
				
			}
			System.out.println();
			
		}

	}

}
